 /**
 * Pokušaj logina
 */
 private void attemptLogin() {
 // Reset errors
 etEmail.setError(null);
 etPassword.setError(null);

 // Dohvati vrijednosti
 String email = etEmail.getText().toString().trim();
 String password = etPassword.getText().toString().trim();

 // Validacija
 boolean hasError = false;

 // Provjeri da li su polja prazna
 if (TextUtils.isEmpty(email)) {
 etEmail.setError("Email je obavezan");
 hasError = true;
 } else if (!isValidEmail(email)) {
 etEmail.setError("Unesite validan email");
 hasError = true;
 } else if (!isEmailDomainValid(email)) {
 etEmail.setError("Email mora biti sa domene fsre.sum.ba");
 hasError = true;
 }

 if (TextUtils.isEmpty(password)) {
 etPassword.setError("Lozinka je obavezna");
 hasError = true;
 } else if (password.length() < 6) {
 etPassword.setError("Lozinka mora imati najmanje 6 karaktera");
 hasError = true;
 }

 if (hasError) {
 return;
 }

 // Pokreni login
 performLogin(email, password);
 }

 /**
 * Izvrši login preko Supabase-a
 */
 private void performLogin(String email, String password) {
