 public void onSuccess(User user) {
 runOnUiThread(() -> {
 showLoading(false);
 Toast.makeText(LoginActivity.this,
 "Dobrodošli, " + user.getFullName() + " (" + user.getRole().getValue() + ")",
 Toast.LENGTH_LONG).show();
 navigateToMainActivity();
 });
 }

 @Override
 public void onError(String error) {
 runOnUiThread(() -> {
 showLoading(false);
 Toast.makeText(LoginActivity.this,
 "Greška: " + error,
 Toast.LENGTH_LONG).show();
 });
 }
 });
 }

 /**
 * Provjeri da li je email validan
 */
 private boolean isValidEmail(String email) {
 return Patterns.EMAIL_ADDRESS.matcher(email).matches();
 }

 /**
 * Provjeri da li email ima tačnu domenu (fsre.sum.ba)
 */
 private boolean isEmailDomainValid(String email) {
 return email.toLowerCase().endsWith(REQUIRED_EMAIL_DOMAIN);
 }

 /**
 * Prikaži/sakrij loading indicator
 */
 private void showLoading(boolean show) {
 if (show) {
 progressBar.setVisibility(View.VISIBLE);
 btnLogin.setEnabled(false);
 etEmail.setEnabled(false);
 etPassword.setEnabled(false);
 } else {
 progressBar.setVisibility(View.GONE);
 btnLogin.setEnabled(true);
 etEmail.setEnabled(true);
 etPassword.setEnabled(true);
 }
 }

 /**
 * Navigiraj na glavni ekran
 */
 private void navigateToMainActivity() {
 Intent intent = new Intent(this, MainActivity.class);
 intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
 startActivity(intent);
 finish();
 }
